'use strict';

// eslint-disable-next-line import/order
const newrelic = require('newrelic');
const path = require('path');
const fs = require('fs');
const swaggerNodeRunnerInstrument = require('feel-newrelic/src/instrumentation/swagger_node_runner');

const newrelicFeel = require('feel-newrelic')(newrelic, {
	exitOnFailure: true,
	rootPath: path.join(path.dirname(__filename), '..'),
});

newrelicFeel.instrumentDatabase();
newrelicFeel.newrelic.instrumentWebframework({
	moduleName: 'swagger-node-runner',
	onRequire: swaggerNodeRunnerInstrument,
	onError: this.errorHandler,
});

const controllerFolder = '/controllers/';
const controllerMethodExtractor = (shim, controller) =>
	Object.getOwnPropertyNames(controller).filter(name =>
		shim.isFunction(controller[name]),
	);

fs.readdirSync(newrelicFeel.config.rootPath + controllerFolder).forEach(
	file => {
		if (path.basename(file) !== 'index.js') {
			const controllerPath = `${
				newrelicFeel.config.rootPath
			}${controllerFolder}${file}`;
			const identifier = `modules.http_api.controllers.${path
				.basename(file)
				.split('.')
				.slice(0, -1)
				.join('.')}`;

			newrelicFeel.instrumentCallbackMethods(
				controllerPath,
				identifier,
				controllerMethodExtractor,
			);
		}
	},
);
